func TestSetup(t *testing.T) {
	var p, _ = new(big.Int).SetString("6806122994068387488588736683", 10)
	q := fmt.Sprint(p)
	var s, _ = new(big.Int).SetString(q, 10)
	fmt.Println(s)
	fmt.Println(p)
	if s.Cmp(p) != 0 {
		t.Fatal("test")
	}
}

